r-md animated fadeInUp">        <div class="container aside-xxl">      <a class="navbar-brand block" href="{{__ROOT__}}">博客网后台管理</a>      <section class="panel panel-default bg-white m-t-lg">        <header class="panel-heading text-center">          <strong>管理员后台登陆</strong>        </header>        <form id="form" onsubmit="return index()" method="post" action="" class="panel-body wrapper-lg">          <div class="form-group">            <label class="control-label">用户名</label>            <input required type="text" class="form-control input-lg" name="username">          </div>          <div class="form-group">            <label class="control-label">密  码</label>            <input required type="password" id="inputPassword" class="form-control input-lg" name="password">          </div>          <div class="form-group">            <label class="control-label">验证码</label>            <input  type="text" id="inputPassword" class="form-control input-lg col-md-6" name="code">            <img src="{{U('code')}}" onclick="this.src=this.src+'&'+Math.random()">          </div>          <button type="submit" class="btn btn-primary">登陆后台</button>        </form>      </section>    </div>  </section>  <!-- footer -->  <footer id="footer">    <div class="text-center padder">      <p>        <small>Powered By houdunwang.com<br> copyright &copy; 2015</small>      </p>    </div>  </footer>  <!-- / footer -->  <script src="resource/admin.ui/js/jquery.min.js"></script>  <!-- Bootstrap -->  <script src="resource/admin.ui/js/bootstrap.js"></script>  <!-- App -->  <script src="resource/admin.ui/js/app.js"></script><!--  <script src="resource/admin.ui/js/slimscroll/jquery.slimscroll.min.js"></script>--><!--    <script src="resource/admin.ui/js/app.plugin.js"></script>--></body></html><script>  function index()  {    var data = $('#form').serialize();    //$.post('请求地址',{发送数据},返回值,返回值的数据类型)    $.post("{{u('login.index')}}",data,function (res) {      if (res.valid){        //登陆成功        util.message(res.message,'index.php?s=admin/index/index','success');      }else{        util.message(res.message,'back','error');      }    },"json");  return false;  }</script>